import React, { useState } from 'react';
import axios from 'axios';
import './chat-bot.css';

export default function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false); // ðŸ‘ˆ track bot typing

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMsg = { role: 'user', content: input };
    setMessages(prev => [...prev, userMsg]);
    setInput('');

    setIsTyping(true); // show typing dots ðŸ‘ˆ

    try {
      const res = await axios.post('http://localhost:8080/api/chat', { messages: [...messages, userMsg] });
      const reply = res.data.reply;

      setMessages(prev => [...prev, { role: 'assistant', content: reply }]);
    } catch (e) {
      setMessages(prev => [...prev, { role: 'assistant', content: 'Error: ' + (e?.message || 'Unknown error') }]);
    } finally {
      setIsTyping(false); // hide typing dots ðŸ‘ˆ
    }
  };

  const onKeyDown = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div className="chat-container">
      <h2 className="chat-title">ByteBuddy</h2>
      <div className="chat-window">
        {messages.map((m, i) => (
          <div key={i} className={`chat-message ${m.role}`}>
            <div className="message-bubble">{m.content}</div>
          </div>
        ))}

        {/* Typing dots when bot is thinking */}
        {isTyping && (
          <div className="chat-message assistant">
            <div className="message-bubble typing-dots">
              <span></span><span></span><span></span>
            </div>
          </div>
        )}
      </div>
      <div className="chat-input">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={onKeyDown}
          placeholder="Type your message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}
