import React, { useState } from 'react';
import axios from 'axios';
import './chat-bot.css';

export default function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMsg = { role: 'user', content: input };
    setMessages(prev => [...prev, userMsg]);

    try {
      const res = await axios.post('http://localhost:8080/api/chat', { messages: [...messages, userMsg] });
      const reply = res.data.reply;
      setMessages(prev => [...prev, { role: 'assistant', content: reply }]);
    } catch (e) {
      setMessages(prev => [...prev, { role: 'assistant', content: '⚠ Error: ' + (e?.message || 'Unknown error') }]);
    } finally {
      setInput('');
    }
  };

  const onKeyDown = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div className="chat-container">
      <h2 className="chat-title">⚡ ByteBuddy</h2>

      <div className="chat-box">
        {messages.map((m, i) => (
          <div key={i} className={`chat-message ${m.role}`}>
            <div className="bubble">{m.content}</div>
          </div>
        ))}
      </div>

      <div className="chat-input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={onKeyDown}
          placeholder="Type your message..."
          className="chat-input"
        />
        <button onClick={sendMessage} className="chat-send">Send</button>
      </div>
    </div>
  );
}
